/**
 * pollRequestStatus
 * uses Get Operation Status
 * GET https://management.core.windows.net/<subscription-id>/operations/<request-id>
 */

pollRequestStatus = function (client, requestId, interval, callback) {
  var checkStatus = function () {
    var path = client.subscriptionId + '/operations/' + requestId;
    client.get(path, function (err, body) {
      if (err) {
        return callback(err);
      }
      switch (body.Status) {
        case 'InProgress':
          setTimeout(checkStatus, interval);
          break;
        case 'Failed':
          callback(body.Error);
          break;
        case 'Succeeded':
          callback(null);
          break;
      }
    });
  };

  checkStatus();
};

getStorageInfoFromUri = exports.getStorageInfoFromUri = function (uri, callback) {
  var u, tokens, path,
    info = {};

  u = URL.parse(uri);
  if (!u.host || !u.path) {
    return callback(errs.create({message: 'invalid Azure container or blob uri'}));
  }

  tokens = u.host.split('.');
  info.storage = tokens[0];

  path = u.path;
  // if necessary, remove leading '/' from path
  if (path.charAt(0) === '/') {
    path = path.substr(1);
  }
  tokens = path.split('/');
  info.container = tokens.shift();
  info.file = tokens.join('/');

  callback(null, info);
};

