  var interval = 4000,
      maxAttempts = 20,
      count = 0,
      options = {
        uri    : this._getCouchPollingUrl(couchName),
        method : 'GET',
        followRedirect: false,
        headers: {
          'Content-Type': 'application/json',
          'User-Agent': util.format('nodejs-pkgcloud/%s', pkgcloud.version)
        }
